import { Link, useHistory } from 'react-router-dom';
import { useState } from "react";
// import './Card.css';

const Card = ({users}) => {
  const history = useHistory();
  let abc = history?.location?.state[0];
  function candidate(candidateemail) {
    return candidateemail.email === abc?.email??'';
  }
  const candidatearray = [users.find(candidate)];
  console.log(candidatearray);
  return (
    <div className="Card">
        <h2 className='cdh1'>Individual ID Card</h2>
        {candidatearray.map((user, key) => {
        return <div key={key} className="cdmember">
          <p className="cdjob">Name: {user.name}</p>
          <p className='cdreq1'>Email: {user.email}</p>
          {/* <p className='cdreq1'>Password: {user.password}</p> */}
          <p className='cdreq1'>Gender: {user.gender}</p>
          <div className='cdreq2'>Father Name: {user.fathername}</div>
          {/* <div className='cdreq2'>Address: {user.address}</div>
          <div className='cdreq3'>Bloodgroup: {user.bloodgroup}</div>
          <div className='cdreq4'>Academic: {user.academic}</div>
          <p className='cdreq5'>Occupation: {user.occupation}</p>
          <p className='cdreq6'>Mobile number: {user.mobilenumber}</p>
          <p className='cdreq7'>City: {user.city}</p> */}
          <button><Link onClick={(e)=>{ history.push({pathname: '/card', state: [{email: user.email}]}) }}>Generate ID Card</Link></button>
        </div>
    })}
    </div>
  );
};

export default Card;
